mapper.select:
select

from tbl_manager_notice


        and id = #{managerNotice.id}


        and system_notice_id = #{managerNotice.systemNoticeId}


        and title like concat('%', #{managerNotice.title}, '%')


        and content like concat('%', #{managerNotice.content}, '%')


        and type = #{managerNotice.type}


        and state = #{managerNotice.state}


        and recipient_id = #{managerNotice.recipientId}


        and manager_id = #{managerNotice.managerId}


        and publish_time = #{managerNotice.publishTime}


        and create_time = #{managerNotice.createTime}


        and update_time = #{managerNotice.updateTime}



    order by ${sort} ${order}

select count(1)
from tbl_manager_notice


        and id = #{id}


        and system_notice_id = #{systemNoticeId}


        and title = #{title}


        and content = #{content}


        and type = #{type}


        and state = #{state}


        and recipient_id = #{recipientId}


        and manager_id = #{managerId}


        and publish_time = #{publishTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}
